mapper.select:
select v.*, (select a.softwareName from app_info a where a.id=v.appId ) as appName, (select valueName from data_dictionary d where v.publishStatus=d.valueId AND d.typeCode='PUBLISH_STATUS') as publishStatusName from app_version v
and v.appId = #{appId}
order by v.creationDate DESC

SELECT v.*,(SELECT `softwareName` FROM `app_info` WHERE v.`appId`=id ) AS appName,(SELECT `valueName` FROM `data_dictionary`
WHERE v.`publishStatus`=`valueId` AND `typeCode`="PUBLISH_STATUS") AS statusName
  FROM `app_version` v WHERE `appId`=#{id}
